INSERT INTO `wiser_module_grids` (`id`, `module_id`, `custom_query`, `count_query`, `grid_options`, `grid_div_id`, `name`, `grid_read_options`) VALUES (1,6001,'SELECT t.template_id,t.parent_id, t.template_name, t.version, t.changed_on, t.published_environment,  CASE WHEN t.template_type = 1 THEN 'HTML' 	WHEN t.template_type = 2 THEN 'CSS'     WHEN t.template_type = 3 THEN 'SCSS'     WHEN t.template_type = 4 THEN 'JS'     WHEN t.template_type = 5 THEN 'QUERY'     WHEN t.template_type = 6 THEN 'NORMAL'     WHEN t.template_type = 7 THEN 'DIRECOTRY'     WHEN t.template_type = 8 THEN 'XML'     WHEN t.template_type = 9 THEN 'ROUTINE' ELSE null END AS template_type,  CASE WHEN test_template.version IS NULL THEN 0 ELSE test_template.version END AS versie_test, CASE WHEN acceptatie_template.version IS NULL THEN 0 ELSE acceptatie_template.version END AS versie_acceptatie, CASE WHEN live_template.version IS NULL THEN 0 ELSE live_template.version END AS versie_live, t.changed_by, m.template_name AS template_parent,  CASE WHEN test_template.version is null THEN 0 ELSE 1 END AS test,CASE WHEN acceptatie_template.version is null THEN 0 ELSE 1 END AS accept, CASE WHEN live_template.version is null THEN 0 ELSE 1 END AS live                 FROM wiser_template t LEFT JOIN wiser_template m ON t.parent_id = m.template_id LEFT JOIN wiser_template test_template ON test_template.template_id = t.template_id AND(test_template.published_environment = 2 OR test_template.published_environment = 6 OR test_template.published_environment = 10 OR test_template.published_environment = 14) AND test_template.template_type != 7              LEFT JOIN wiser_template acceptatie_template ON acceptatie_template.template_id = t.template_id AND(acceptatie_template.published_environment = 4 OR acceptatie_template.published_environment = 6 OR acceptatie_template.published_environment = 12 OR acceptatie_template.published_environment = 14) AND acceptatie_template.template_type != 7 LEFT JOIN wiser_template live_template ON live_template.template_id = t.template_id AND(live_template.published_environment = 8 OR live_template.published_environment = 10 OR live_template.published_environment = 12 OR live_template.published_environment = 14) AND live_template.template_type != 7        LEFT JOIN wiser_template test ON test.template_id = t.template_id AND test.version = t.version AND test.published_environment != 0        LEFT JOIN wiser_template x ON x.template_id = t.template_id AND x.version = t.version WHERE t.version = (SELECT MAX(version) FROM wiser_template x2 WHERE x2.template_id = t.template_id)       AND t.published_environment != 14 AND NOT EXISTS(SELECT * FROM wiser_commit_template wdt WHERE wdt.template_id = t.template_id and wdt.version = t.version) AND t.parent_id != '' {sort}', NULL, '{                 	"gridViewMode": true,     	"gridViewSettings": {      		"selectable": "multiple, row",     		"pageSize": 100,      		"columns": [                			{ "selectable": "true", "width": "50px"},     			{ "field": "template_id", "title": "ID", "width": "100px"},     			{ "field": "template_type", "title": "Type", "width": "150px" },     			{ "field": "template_parent", "title": "Parent", "width": "150px" },      			{ "field": "template_name", "title": "Template" },     			{ "field": "version", "title": "Versie", "width": "150px" },     			{ "field": "versie_live", "title": "Versie live", "width": "150px" },    			{ "field": "versie_acceptatie", "title": "Versie acceptatie", "width": "150px" },                 			{ "field": "versie_test", "title": "Versie test", "width": "150px" },     			{ "field": "changed_on", "format":"{0:F}", "title": "Datum" },     			{ "field": "changed_by", "title": "Door" }            		]          	}           }', '#gridView', 'Template Commit Grid', '{"page":1,"pageSize":100,"skip":0,"take":100,"firstLoad":true, "Sort":[{"Field":"changed_on","Dir":"desc"},{"Field":"template_id","Dir":"asc"}, {"Field":"version","Dir":"asc"}]}');
INSERT INTO `wiser_module_grids` (`id`, `module_id`, `custom_query`, `count_query`, `grid_options`, `grid_div_id`, `name`, `grid_read_options`) VALUES (2,6001,'SELECT result.*, CASE result.published_environment WHEN 2 THEN 1 WHEN 6 THEN 1 WHEN 10 THEN 1 WHEN 14 THEN 1 ELSE 0 END AS test, CASE result.published_environment WHEN 4 THEN 1 WHEN 6 THEN 1 WHEN 12 THEN 1 WHEN 14 THEN 1 ELSE 0 END AS accept, CASE result.published_environment WHEN 8 THEN 1 WHEN 10 THEN 1 WHEN 12 THEN 1 WHEN 14 THEN 1 ELSE 0 END AS live, CONCAT(\'\', group_concat(usefull_data), \'\') as commit_data,CONCAT(\'(\', group_concat(data1 SEPARATOR \';\'), \')\') as content_data \n         FROM(\n             select wiser_commit.id, wiser_commit.description, wiser_commit.added_on, wiser_commit.changed_by, wiser_commit.completed, wdc.published_environment, group_concat(wdc.component separator \'\n               \') AS usefull_data, GROUP_CONCAT(CONCAT(wdc.component,\',\',wdc.component_mode) SEPARATOR \'; \') as data1\n               FROM wiser_commit        	\n               INNER JOIN wiser_commit_dynamic_content wcdc ON wiser_commit.id = wcdc.commit_id       	\n               INNER JOIN wiser_dynamic_content wdc ON wcdc.dynamic_content_id = wdc.content_id AND wcdc.version = wdc.version\n               WHERE wcdc.version = (SELECT MAX(version) FROM wiser_commit_dynamic_content as wcdc2 WHERE wcdc2.dynamic_content_id = wcdc.dynamic_content_id AND wcdc.commit_id = wcdc2.commit_id)\n               AND wdc.published_environment != 14 AND wdc.published_environment != 12 AND wdc.published_environment != 10 AND wdc.published_environment != 8 AND wdc.published_environment != 0\n               group by wiser_commit.id      \n                               \n               union all                  \n              select wiser_commit.id, wiser_commit.description, wiser_commit.added_on, wiser_commit.changed_by, wiser_commit.completed, wt.published_environment, group_concat(wt.template_name separator \'\n               \') AS usefull_data, GROUP_CONCAT(CONCAT(wt2.template_name,\',\',wt.template_name)SEPARATOR \'; \') as data1\n               FROM wiser_commit         \n               INNER JOIN wiser_commit_template wct ON wiser_commit.id = wct.commit_id         \n               INNER JOIN wiser_template wt ON wct.template_id = wt.template_id AND wct.version = wt.version\n       		LEFT JOIN wiser_template wt2 ON wt.parent_id = wt2.template_id  \n               WHERE wct.version = (SELECT MAX(version) FROM wiser_commit_template as wct2 WHERE wct2.template_id = wct.template_id AND wct.commit_id = wct2.commit_id)\n               AND wt.published_environment != 14 AND wt.published_environment != 12 AND wt.published_environment != 10 AND wt.published_environment != 8  AND wt.published_environment != 0\n               group by wiser_commit.id) AS result      \n               WHERE result.completed = false\n               group by result.id\n               {sort}',NULL,'{        \n       	\"gridViewMode\": true,            \n       	\"gridViewSettings\": {            \n       		\"pageSize\": 100,                \n               \"selectable\": \"multiple, row\",                    \n               \"columns\": [                       \n       			{ \"selectable\": \"true\", \"width\": \"50px\"},      \n                { \"field\": \"id\", \"title\": \"ID\" },  \n                   { \"field\": \"description\", \"title\": \"Commit Message\" },                        \n                   { \"field\": \"added_on\", \"format\":\"{0:F}\",\"title\": \"Datum\" },                       \n                   { \"field\": \"live\", \"title\": \"Live\",\"width\":\"100px\" },                        \n                   { \"field\": \"accept\", \"title\": \"Acceptatie\",\"width\":\"100px\" },                    \n                   { \"field\": \"test\", \"title\": \"Test\",\"width\":\"100px\" },                 \n                   { \"field\": \"changed_by\", \"title\": \"Door\", \"width\": \"200px\" },		\n                   { \"field\": \"content_data\", \"title\": \"Content\",\"width\":\"500px\", \"class\":\"test\" }                   \n       		]        \n       	}    \n       }','#deploygrid','Deploy Grid','{\"page\":1,\"pageSize\":100,\"skip\":0,\"take\":100,\"firstLoad\":true, \"Sort\":[{\"Field\":\"added_on\",\"Dir\":\"desc\"}]}');
INSERT INTO `wiser_module_grids` (`id`, `module_id`, `custom_query`, `count_query`, `grid_options`, `grid_div_id`, `name`, `grid_read_options`) VALUES (3,6001,'SELECT t.content_id, t.component, t.component_mode, t.version, t.changed_on, t.published_environment, t.changed_by, \nCASE WHEN test_content.version is null THEN 0 ELSE test_content.version END AS test, \nCASE WHEN acceptatie_content.version is null THEN 0 ELSE acceptatie_content.version END AS accept, \nCASE WHEN live_content.version is null THEN 0 ELSE live_content.version END AS live                    \n    FROM wiser_dynamic_content t        \n    LEFT JOIN wiser_dynamic_content test_content ON test_content.content_id = t.content_id AND(test_content.published_environment = 2 OR test_content.published_environment = 6 OR test_content.published_environment = 10 OR test_content.published_environment = 14)        \n    LEFT JOIN wiser_dynamic_content acceptatie_content ON acceptatie_content.content_id = t.content_id AND(acceptatie_content.published_environment = 4 OR acceptatie_content.published_environment = 6 OR acceptatie_content.published_environment = 12 OR acceptatie_content.published_environment = 14)        \n    LEFT JOIN wiser_dynamic_content live_content ON live_content.content_id = t.content_id AND(live_content.published_environment = 8 OR live_content.published_environment = 10 OR live_content.published_environment = 12 OR live_content.published_environment = 14)        \n    WHERE t.version = (SELECT MAX(version) FROM wiser_dynamic_content x2 WHERE x2.content_id = t.content_id)        \n    AND NOT EXISTS(SELECT * FROM wiser_commit_dynamic_content dt WHERE dt.dynamic_content_id = t.content_id and dt.version = t.version)\n    {sort}',NULL,'\n {        \n 	\"gridViewMode\": true,           \n     \"gridViewSettings\": {            \n 		\"pageSize\": 100,                \n         \"selectable\": \"multiple, row\",                    \n         \"columns\": [                       \n 			{ \"selectable\": \"true\", \"width\": \"50px\"},    \n             { \"field\": \"content_id\", \"title\": \"ID\", \"width\": \"100px\" },                \n             { \"field\": \"component\", \"title\": \"Component\" },                  \n             { \"field\": \"component_mode\", \"title\": \"Component mode\" },                 \n             { \"field\": \"version\", \"title\": \"Versie\", \"width\": \"100px\"  },                     \n             { \"field\": \"changed_on\", \"format\":\"{0:F}\", \"title\": \"Aangepast op\" }, \n             { \"field\": \"test\", \"title\": \"Test\", \"width\": \"100px\"  },\n             { \"field\": \"accept\", \"title\": \"Acceptatie\", \"width\": \"100px\"  },\n             { \"field\": \"live\", \"title\": \"Live\", \"width\": \"100px\"  },                   \n             { \"field\": \"changed_by\", \"title\": \"Door\" }                  \n 		]      \n 	}   \n }\n         ','#dynamicContentGrid','Dynamic Content Commit Grid','{\"page\":1,\"pageSize\":100,\"skip\":0,\"take\":100,\"firstLoad\":true, \"Sort\":[{\"Field\":\"changed_on\",\"Dir\":\"desc\"},{\"Field\":\"content_id\",\"Dir\":\"asc\"},{\"Field\":\"version\",\"Dir\":\"asc\"}]}');
INSERT INTO `wiser_module_grids` (`id`, `module_id`, `custom_query`, `count_query`, `grid_options`, `grid_div_id`, `name`, `grid_read_options`) VALUES (4,6001,'SELECT t.*, CASE WHEN t.template_type = 1 THEN \'HTML\'\n	WHEN t.template_type = 2 THEN \'CSS\'\n    WHEN t.template_type = 3 THEN \'SCSS\'\n    WHEN t.template_type = 4 THEN \'JS\'\n    WHEN t.template_type = 5 THEN \'QUERY\'\n    WHEN t.template_type = 6 THEN \'NORMAL\'\n    WHEN t.template_type = 7 THEN \'DIRECOTRY\'\n    WHEN t.template_type = 8 THEN \'XML\'\n    WHEN t.template_type = 9 THEN \'ROUTINE\' ELSE null END AS template_type_text, wc.added_on AS commit_date, m.template_name AS template_parent,test_content.version AS test_version, acceptatie_content.version AS accepatatie_version, live_content.version AS live_version, wct.added_on, CASE WHEN test_content.version is null THEN 0 ELSE test_content.version END AS test,CASE WHEN acceptatie_content.version is null THEN 0 ELSE acceptatie_content.version END AS accept, CASE WHEN live_content.version is null THEN 0 ELSE live_content.version END AS live\n           FROM wiser_template t        \n           LEFT JOIN wiser_template m ON t.parent_id = m.template_id \n           LEFT JOIN wiser_commit_template wct ON wct.template_id = t.template_id AND wct.version = t.version        \n           LEFT JOIN wiser_template test_content ON test_content.template_id = t.template_id AND(test_content.published_environment = 2 OR test_content.published_environment = 6 OR test_content.published_environment = 10 OR test_content.published_environment = 14)\n           LEFT JOIN wiser_template acceptatie_content ON acceptatie_content.template_id = t.template_id AND(acceptatie_content.published_environment = 4 OR acceptatie_content.published_environment = 6 OR acceptatie_content.published_environment = 12 OR acceptatie_content.published_environment = 14)\n           LEFT JOIN wiser_template live_content ON live_content.template_id = t.template_id AND(live_content.published_environment = 8 OR live_content.published_environment = 10 OR live_content.published_environment = 12 OR live_content.published_environment = 14)    \n           LEFT JOIN wiser_commit wc ON wct.commit_id = wc.id\n           WHERE EXISTS(SELECT * FROM wiser_commit_template wct2 WHERE t.template_id = wct2.template_id and t.version = wct2.version)\n			{sort}\n  ',NULL,'  {\n       \"gridViewMode\": true,\n       \"checkboxes\": true,\n       \"gridViewSettings\": {\n   		\"selectable\": \"row\",\n           \"pageSize\": 100,\n           \"columns\": [\n   			{ \"field\": \"template_id\", \"title\": \"ID\" },\n               { \"field\": \"template_type_text\", \"title\": \"Type\" },\n               { \"field\": \"template_parent\", \"title\": \"Parent\" },\n               { \"field\": \"template_name\", \"title\": \"Template\" },\n               { \"field\": \"version\", \"title\": \"Versie\" },\n               { \"field\": \"test\", \"title\": \"Versie Test\" },\n               { \"field\": \"accept\", \"title\": \"Versie Accept\" },\n               { \"field\": \"live\", \"title\": \"Versie Live\" },\n               { \"field\": \"commit_date\", \"format\":\"{0:F}\",\"title\": \"Datum\" }\n   		]\n   	}\n   }','#historyGridId','History Grid','{\"page\":1,\"pageSize\":100,\"skip\":0,\"take\":100,\"firstLoad\":true, \"Sort\":[{\"Field\":\"commit_date\",\"Dir\":\"desc\"}]}');
