INSERT INTO `wiser_module` (`id`, `custom_query`, `count_query`, `options`, `name`, `icon`, `color`, `type`, `group`) VALUES (5524, 'SELECT\r\n	x.*,\r\n	permission.*\r\nFROM (\r\n	SELECT\r\n		`import`.id,\r\n		`import`.`name`,\r\n		`import`.start_on,\r\n		`import`.started_on,\r\n		`import`.finished_on,\r\n		`import`.success,\r\n		JSON_LENGTH(`import`.`data`) AS amount_of_items,\r\n		SUM(JSON_LENGTH(jsonTable.links)) AS amount_of_links,\r\n		SUM(JSON_LENGTH(jsonTable.files)) AS amount_of_files,\r\n		TIMEDIFF(`import`.finished_on, `import`.started_on) AS time_taken,\r\n		`import`.errors,\r\n		`import`.user_id\r\n	FROM wiser_import AS `import`,\r\n	JSON_TABLE(`import`.`data`, \r\n		\"$[*]\"\r\n		COLUMNS(\r\n			rowid FOR ORDINALITY,\r\n			links JSON PATH \"$.Links\" DEFAULT \'[]\' ON EMPTY,\r\n			files JSON PATH \"$.Files\" DEFAULT \'[]\' ON EMPTY\r\n		)\r\n	) AS jsonTable\r\n	GROUP BY `import`.id\r\n	ORDER BY start_on DESC\r\n) AS x\r\nLEFT JOIN wiser_user_roles AS userRole ON userRole.user_id = ?userId\r\nLEFT JOIN wiser_permission AS permission ON permission.role_id = userRole.role_id AND permission.module_id = ?id\r\nWHERE permission.permissions IS NULL OR permission.permissions & 4 = 4 OR x.user_id = ?userId\r\nGROUP BY x.id', 'SELECT COUNT(*) FROM wiser_import', '{\r\n    \"gridViewMode\": true,\r\n    \"gridViewSettings\": {\r\n        \"pageSize\": 100,\r\n        \"hideCommandColumn\": true,\r\n        \"toolbar\": {\r\n            \"hideExportButton\": true,\r\n			\"hideCreateButton\": true\r\n        },\r\n		\"disableOpeningOfItems\": true,\r\n        \"columns\": [{\r\n                \"field\": \"name\",\r\n                \"title\": \"Naam\"\r\n            }, {\r\n                \"field\": \"start_on\",\r\n                \"title\": \"Gekozen startdatumtijd\",\r\n				\"format\": \"{0:dd-MM-yyyy HH:mm:ss}\"\r\n            }, {\r\n                \"field\": \"started_on\",\r\n                \"title\": \"Begonnen om\",\r\n				\"format\": \"{0:dd-MM-yyyy HH:mm:ss}\"\r\n            }, {\r\n                \"field\": \"finished_on\",\r\n                \"title\": \"Afgerond om\",\r\n				\"format\": \"{0:dd-MM-yyyy HH:mm:ss}\"\r\n            }, {\r\n                \"field\": \"success\",\r\n                \"title\": \"gelukt\",\r\n				\"template\": \"# if (typeof success === \'boolean\') { # <span class=\'k-icon k-i-#:(success ? \\\"check k-syntax-str\\\" : \\\"cancel k-syntax-error\\\")#\'></span> # } #\"\r\n            }, {\r\n                \"field\": \"amount_of_items\",\r\n                \"title\": \"Aantal items\"\r\n            }, {\r\n                \"field\": \"amount_of_links\",\r\n                \"title\": \"Aantal koppelingen\"\r\n            }, {\r\n                \"field\": \"amount_of_files\",\r\n                \"title\": \"Aantal bestanden\"\r\n            }, {\r\n                \"field\": \"time_taken\",\r\n                \"title\": \"Duur\"\r\n            },\r\n			{\r\n                \"title\": \"Fouten\",\r\n                \"encoded\": false,\r\n				\"template\": \"# if(!errors) { # Geen fouten # } else { # <a class=\'k-button k-grid-settings\' onclick=\'var kendoWindow = $(`<div id=\\\"importErrorLogsPopup\\\">`).kendoWindow({ width: \\\"600px\\\", title: \\\"Fouten\\\", content: { template: \\\"<iframe frameborder=0 class=iframe width=100% height=100% />\\\" }}).data(\\\"kendoWindow\\\"); kendoWindow.center().open(); var iframe = kendoWindow.element.find(\\\"iframe\\\")[0]; iframe = iframe.contentWindow || (iframe.contentDocument.document || iframe.contentDocument); iframe.document.open(); iframe.document.write(\\\"<pre>\\\" + dynamicItems.grids.mainGrid.dataItem(this.closest(\\\"tr\\\")).errors + \\\"</pre>\\\"); iframe.document.close();\'>Fouten bekijken <span class=\'k-icon k-i-hyperlink-open\'></span></a> # } #\"\r\n            }\r\n        ]\r\n    }\r\n}\r\n', 'Importhistorie', 'database', NULL, 'DynamicItems', 'Contentbeheer');
INSERT INTO `wiser_module` (`id`, `custom_query`, `count_query`, `options`, `name`, `icon`, `color`, `type`) VALUES (5534, 'SELECT\r\n	redirect.id,\r\n	redirect.id AS `encrypted_id_encrypt_withdate`,\r\n	redirect.entity_type,\r\n	oldurl.`value` AS oldurl,\r\n	newurl.`value` AS newurl,\r\n	permanent.`value` AS permanent,\r\n	ordering.`value` AS ordering\r\nFROM wiser_item redirect\r\n[if({oldurl}=)]LEFT[endif] JOIN wiser_itemdetail oldurl ON oldurl.item_id=redirect.id AND oldurl.`key`=\'oldurl\' [if({oldurl}!)]AND oldurl.`value` {oldurl_filter}[endif]\r\n[if({newurl}=)]LEFT[endif] JOIN wiser_itemdetail newurl ON newurl.item_id=redirect.id AND newurl.`key`=\'newurl\' [if({newurl}!)]AND newurl.`value` {newurl_filter}[endif]\r\n[if({permanent}=)]LEFT[endif] JOIN wiser_itemdetail permanent ON permanent.item_id=redirect.id AND permanent.`key`=\'permanent\' [if({permanent}!)]AND permanent.`value` {permanent_filter}[endif]\r\n[if({ordering}=)]LEFT[endif] JOIN wiser_itemdetail ordering ON ordering.item_id=redirect.id AND ordering.`key`=\'ordering\' [if({ordering}!)]AND ordering.`value` {ordering_filter}[endif]\r\nWHERE redirect.entity_type=\'redirect\'', 'SELECT COUNT(*)\r\nFROM wiser_item redirect\r\n[if({oldurl}!)]JOIN wiser_itemdetail oldurl ON oldurl.item_id=redirect.id AND oldurl.`key`=\'oldurl\' AND oldurl.`value` {oldurl_filter}[endif]\r\n[if({newurl}!)]JOIN wiser_itemdetail newurl ON newurl.item_id=redirect.id AND newurl.`key`=\'newurl\' AND newurl.`value` {newurl_filter}[endif]\r\n[if({permanent}!)]JOIN wiser_itemdetail permanent ON permanent.item_id=redirect.id AND permanent.`key`=\'permanent\' AND permanent.`value` {permanent_filter}[endif]\r\n[if({ordering}!)]JOIN wiser_itemdetail ordering ON ordering.item_id=redirect.id AND ordering.`key`=\'ordering\' AND ordering.`value` {ordering_filter}[endif]\r\nWHERE redirect.entity_type=\'redirect\'', '{\r\n    \"gridViewMode\": true,\r\n    \"gridViewSettings\": {\r\n			\"skipNameForNewItems\": true,\r\n			\"showDeleteButton\": true,\r\n			\"pageSize\": 100,\r\n			\"hideCommandColumn\": false,\r\n			\"toolbar\": {\r\n					\"hideExportButton\": false,\r\n					\"hideCreateButton\": false\r\n			},\r\n			\"disableOpeningOfItems\": false,\r\n			\"columns\": [\r\n				{ \"field\": \"oldurl\", \"title\": \"Oude URL\" },\r\n				{ \"field\": \"newurl\", \"title\": \"Nieuwe URL\" },\r\n				{ \"field\": \"permanent\", \"title\": \"Permanent\" },\r\n				{ \"field\": \"ordering\", \"title\": \"Volgnummer\" }\r\n			]\r\n    }\r\n}', 'Redirect', 'reset', NULL, 'DynamicItems');
