INSERT INTO `wiser_module` (id, `custom_query`, `count_query`, `options`, `name`, `icon`, `type`, `group`) VALUES (854, 'SELECT \r\n	configuration.id, \r\n	configuration.id AS encrypted_id_encrypt_withdate, \r\n	configuration.title, \r\n	configuration.entity_type, \r\n	configuration.added_on AS	datecreated \r\nFROM wiser_item AS configuration \r\n# Check permissions. Default permissions are everything enabled, so if the user has no role or the role has no permissions on this item, they are allowed everything. \r\nJOIN wiser_itemlink AS configuratie_onder_leverancier ON configuratie_onder_leverancier.item_id = configuration.id \r\nLEFT JOIN wiser_user_roles user_role ON user_role.user_id = ?userId \r\nLEFT JOIN wiser_permission permission ON permission.role_id = user_role.role_id AND permission.item_id = configuratie_onder_leverancier.destination_item_id \r\nWHERE configuration.entity_type = \'configuration\' \r\nAND (permission.id IS NULL OR (permission.permissions & 1) > 0) \r\n[if({id}!)]AND configuration.id {id_filter}[endif] \r\n[if({title}!)]AND configuration.title {title_filter}[endif] \r\n[if({datecreated}!)]AND configuration.added_on {datecreated_filter}[endif] \r\nORDER BY configuration.id DESC \r\n{limit}','','{ \r\n	\"gridViewMode\": true, \r\n	\"gridViewSettings\": { \r\n		\"pageSize\": 100, \r\n		\"columns\": [ \r\n			{ \"field\": \"id\", \"title\": \"ID\", \"filterable\": { \"extra\": false } }, \r\n		{ \"field\": \"title\", \"title\": \"Naam\",  \"filterable\": { \"extra\": false }}, \r\n			{ \"field\": \"datecreated\", \"title\": \"Aangemaakt op\", \"filterable\": { \"extra\": false } } \r\n		], \r\n		\"filterable\": true \r\n	} \r\n}','Configuraties','routes','DynamicItems','Bestelproces');
