INSERT IGNORE INTO `easy_objects` (typenr, `key`, `value`) VALUES (-1,'W2CHECKOUT_ShippingCostsQuery',REPLACE('SET @orderamount = {price~product~In_VAT_In_Discount~0.00~en-US};\n SET @deliverymethod = \'{shippingMethod}\';\n SET @country = \'{land}\';\n SET @postalcode = SUBSTRING(TRIM(\'{postcode}\'),1,4);\n SET @weight = \'{weight}\';\n \n SELECT\n costs.id,\n costs.`value` AS costs,\n type.`value` AS type,\n includesvat.`value` AS includesvat,\n vatrate.`value` AS vatrate,\n propertyname.`value` AS propertyname,\n shippingcost.title AS title,\n country.id AS country,\n postalcodefrom.id AS postalcodefrom,\n postalcodeto.id AS postalcodeto,\n weightfrom.id AS weightfrom,\n weightto.id AS weightto,\n orderamountstart.id AS orderamountstart,\n orderamountend.id AS orderamountend,\n deliverymethod.id AS deliverymethod \n \n FROM wiser_item shippingcost\n INNER JOIN wiser_itemlink AS shippingcost_to_folder ON shippingcost_to_folder.item_id=shippingcost.id AND shippingcost_to_folder.destination_item_id=<shippingCostDirectoryId>\n \n # Beperkingen\n LEFT JOIN wiser_itemdetail orderamountstart ON orderamountstart.item_id=shippingcost.id AND orderamountstart.`key`=\'orderamountstart\'\n LEFT JOIN wiser_itemdetail orderamountend ON orderamountend.item_id=shippingcost.id AND orderamountend.`key`=\'orderamountend\'\n LEFT JOIN wiser_itemdetail deliverymethod ON deliverymethod.item_id=shippingcost.id AND deliverymethod.`key`=\'deliverymethod\'\n LEFT JOIN wiser_itemdetail country ON country.item_id=shippingcost.id AND country.`key`=\'country\'\n LEFT JOIN wiser_itemdetail postalcodefrom ON postalcodefrom.item_id=shippingcost.id AND postalcodefrom.`key`=\'postalcode_from\'\n LEFT JOIN wiser_itemdetail postalcodeto ON postalcodeto.item_id=shippingcost.id AND postalcodeto.`key`=\'postalcode_to\'\n LEFT JOIN wiser_itemdetail weightfrom ON weightfrom.item_id=shippingcost.id AND weightfrom.`key`=\'weight_from\'\n LEFT JOIN wiser_itemdetail weightto ON weightto.item_id=shippingcost.id AND weightto.`key`=\'weight_to\'\n \n # Gevens die geselecteerd moeten worden\n LEFT JOIN wiser_itemdetail costs ON costs.item_id=shippingcost.id AND costs.`key`=\'costs\'\n LEFT JOIN wiser_itemdetail type ON type.item_id=shippingcost.id AND type.`key`=\'type\'\n LEFT JOIN wiser_itemdetail includesvat ON includesvat.item_id=shippingcost.id AND includesvat.`key`=\'includesvat\'\n LEFT JOIN wiser_itemdetail vatrate ON vatrate.item_id=shippingcost.id AND vatrate.`key`=\'vatrate\'\n LEFT JOIN wiser_itemdetail propertyname ON propertyname.item_id=shippingcost.id AND propertyname.`key`=\'propertyname\'\n \n WHERE shippingcost.entity_type=\'shippingcost\'\n \n AND IF(IFNULL(orderamountstart.`value`,\'\')=\'\',1=1, REPLACE(orderamountstart.`value`,\',\',\'.\')<=@orderamount)\n AND IF(IFNULL(orderamountend.`value`,\'\')=\'\',1=1,REPLACE(orderamountend.`value`,\',\',\'.\')>@orderamount)\n AND IF(IFNULL(deliverymethod.`value`,\'\')=\'\',1=1, deliverymethod.`value`=@deliverymethod)\n AND IF(IFNULL(country.`value`,\'\')=\'\',1=1, country.`value`=@country)\n AND IF(IFNULL(postalcodefrom.`value`,\'\')=\'\',1=1, postalcodefrom.`value`<=@postalcode)\n AND IF(IFNULL(postalcodeto.`value`,\'\')=\'\',1=1, postalcodeto.`value`>=@postalcode)\n AND IF(IFNULL(weightfrom.`value`,\'\')=\'\',1=1, weightfrom.`value`<=REPLACE(@weight,\',\',\'.\')*1)\n AND IF(IFNULL(weightto.`value`,\'\')=\'\',1=1, weightto.`value`>=REPLACE(@weight,\',\',\'.\')*1)\n \n ORDER BY country.id DESC, postalcodefrom.id DESC, postalcodeto.id DESC, weightfrom.id DESC, weightto.id DESC, orderamountstart.id DESC, orderamountend.id DESC, deliverymethod.id DESC\n \n LIMIT 1;\n','<shippingCostDirectoryId>',@shippingCostDirectoryId));
