INSERT IGNORE INTO `easy_objects` (typenr, `key`, `value`) VALUES (-1,'W2CHECKOUT_PaymentMethodsQuery','SET @orderamount = {price~product~In_VAT_In_Discount~0.00~en-US};\n SET @country = \'{land}\';\n \n SELECT\n  paymentmethod.id,\n  friendlyname.`value` AS friendlyname,\n  paymentcode.`value` AS paymentcode,\n  class.`value` AS class,\n  description.`value` AS description,\n  REPLACE(ROUND(REPLACE(IFNULL(costs.`value`,\'0\'),\',\',\'.\')*1.21,2),\'.\',\',\') AS costs\n FROM wiser_item paymentmethod\n JOIN wiser_itemlink paymentorder ON paymentorder.item_id=paymentmethod.id AND paymentorder.type=1\n \n # Beperkingen\n LEFT JOIN wiser_itemdetail orderamountstart ON orderamountstart.item_id=paymentmethod.id AND orderamountstart.`key`=\'orderamountstart\'\n LEFT JOIN wiser_itemdetail orderamountend ON orderamountend.item_id=paymentmethod.id AND orderamountend.`key`=\'orderamountend\'\n LEFT JOIN wiser_itemdetail country ON country.item_id=paymentmethod.id AND country.`key`=\'country\'\n \n # Gevens die geselecteerd moeten worden\n LEFT JOIN wiser_itemdetail friendlyname ON friendlyname.item_id=paymentmethod.id AND friendlyname.`key`=\'name\'\n LEFT JOIN wiser_itemdetail paymentcode ON paymentcode.item_id=paymentmethod.id AND paymentcode.`key`=\'code\'\n LEFT JOIN wiser_itemdetail class ON class.item_id=paymentmethod.id AND class.`key`=\'class\'\n LEFT JOIN wiser_itemdetail costs ON costs.item_id=paymentmethod.id AND costs.`key`=\'costs\'\n LEFT JOIN wiser_itemdetail ispercentage ON ispercentage.item_id=paymentmethod.id AND ispercentage.`key`=\'ispercentage\'\n LEFT JOIN wiser_itemdetail includesvat ON includesvat.item_id=paymentmethod.id AND includesvat.`key`=\'includesvat\'\n LEFT JOIN wiser_itemdetail vatrate ON vatrate.item_id=paymentmethod.id AND vatrate.`key`=\'vatrate\'\n LEFT JOIN wiser_itemdetail description ON description.item_id=paymentmethod.id AND description.`key`=\'description\'\n \n WHERE paymentmethod.entity_type=\'paymentmethod\' AND paymentmethod.published_environment>0\n AND IF(IFNULL(orderamountstart.`value`,\'\')=\'\',1=1, REPLACE(orderamountstart.`value`,\',\',\'.\')<=@orderamount)\n AND IF(IFNULL(orderamountend.`value`,\'\')=\'\',1=1,REPLACE(orderamountend.`value`,\',\',\'.\')>@orderamount)\n AND IF(IFNULL(country.`value`,\'\')=\'\',1=1, country.`value`=@country)\n \n ORDER BY paymentorder.ordering\n');
INSERT IGNORE INTO `easy_objects` (typenr, `key`, `value`) VALUES (-1,'W2CHECKOUT_PaymentCostsQuery','SET @paymentMethod = \'{paymentmethod}\';\n \n SELECT\n  paymentmethod.id,\n  friendlyname.`value` AS friendlyname,\n  costs.`value` AS costs,\n  ispercentage.`value` AS ispercentage,\n  includesvat.`value` AS includesvat,\n	  vatrate.`value` AS vatrate\n FROM wiser_item paymentmethod\n JOIN wiser_itemdetail paymentcode ON paymentcode.item_id=paymentmethod.id AND paymentcode.`key`=\'code\' AND paymentcode.`value`=@paymentMethod\n \n  # Beperkingen\n LEFT JOIN wiser_itemdetail orderamountstart ON orderamountstart.item_id=paymentmethod.id AND orderamountstart.`key`=\'orderamountstart\'\n LEFT JOIN wiser_itemdetail orderamountend ON orderamountend.item_id=paymentmethod.id AND orderamountend.`key`=\'orderamountend\'\n LEFT JOIN wiser_itemdetail country ON country.item_id=paymentmethod.id AND country.`key`=\'country\'\n \n # Gevens die geselecteerd moeten worden\n LEFT JOIN wiser_itemdetail friendlyname ON friendlyname.item_id=paymentmethod.id AND friendlyname.`key`=\'name\'\n LEFT JOIN wiser_itemdetail costs ON costs.item_id=paymentmethod.id AND costs.`key`=\'costs\'\n LEFT JOIN wiser_itemdetail ispercentage ON ispercentage.item_id=paymentmethod.id AND ispercentage.`key`=\'ispercentage\'\n LEFT JOIN wiser_itemdetail includesvat ON includesvat.item_id=paymentmethod.id AND includesvat.`key`=\'includesvat\'\n LEFT JOIN wiser_itemdetail vatrate ON vatrate.item_id=paymentmethod.id AND vatrate.`key`=\'vatrate\'\n \n WHERE paymentmethod.entity_type=\'paymentmethod\' AND paymentmethod.published_environment>0\n');
