FROM wiser_item shippingcost\n JOIN wiser_itemdetail deliverymethodid ON deliverymethodid.item_id=shippingcost.id AND deliverymethodid.`key`=\'deliverymethod\'\n JOIN wiser_item deliverymethod ON deliverymethod.id=deliverymethodid.`value`  JOIN wiser_itemlink AS shippingcost_to_folder ON shippingcost_to_folder.item_id=shippingcost.id AND shippingcost_to_folder.type=1 AND shippingcost_to_folder.destination_item_id=<shippingCostDirectoryId> \n \n # Beperkingen\n LEFT JOIN wiser_itemdetail orderamountstart ON orderamountstart.item_id=shippingcost.id AND orderamountstart.`key`=\'orderamountstart\'\n LEFT JOIN wiser_itemdetail orderamountend ON orderamountend.item_id=shippingcost.id AND orderamountend.`key`=\'orderamountend\'\n LEFT JOIN wiser_itemdetail country ON country.item_id=shippingcost.id AND country.`key`=\'country\'\n LEFT JOIN wiser_itemdetail postalcodefrom ON postalcodefrom.item_id=shippingcost.id AND postalcodefrom.`key`=\'postalcode_from\'\n LEFT JOIN wiser_itemdetail postalcodeto ON postalcodeto.item_id=shippingcost.id AND postalcodeto.`key`=\'postalcode_to\'\n LEFT JOIN wiser_itemdetail weightfrom ON weightfrom.item_id=shippingcost.id AND weightfrom.`key`=\'weight_from\'\n LEFT JOIN wiser_itemdetail weightto ON weightto.item_id=shippingcost.id AND weightto.`key`=\'weight_to\'\n \n # Gevens die geselecteerd moeten worden\n LEFT JOIN wiser_itemdetail description ON description.item_id=deliverymethod.id AND description.`key`=\'description\'\n LEFT JOIN wiser_itemdetail subdescription ON subdescription.item_id=deliverymethod.id AND subdescription.`key`=\'subdescription\'\n LEFT JOIN wiser_itemdetail costs ON costs.item_id=shippingcost.id AND costs.`key`=\'costs\'\n \n WHERE shippingcost.entity_type=\'shippingcost\'\n AND IF(IFNULL(orderamountstart.`value`,\'\')=\'\',1=1, REPLACE(orderamountstart.`value`,\',\',\'.\')<=@orderamount)\n AND IF(IFNULL(orderamountend.`value`,\'\')=\'\',1=1,REPLACE(orderamountend.`value`,\',\',\'.\')>@orderamount)\n AND IF(IFNULL(country.`value`,\'\')=\'\',1=1, country.`value`=@country)\n AND IF(IFNULL(postalcodefrom.`value`,\'\')=\'\',1=1, postalcodefrom.`value`<=@postalcode)\n AND IF(IFNULL(postalcodeto.`value`,\'\')=\'\',1=1, postalcodeto.`value`>=@postalcode)\n AND IF(IFNULL(weightfrom.`value`,\'\')=\'\',1=1, weightfrom.`value`<=REPLACE(@weight,\',\',\'.\')*1)\n AND IF(IFNULL(weightto.`value`,\'\')=\'\',1=1, weightto.`value`>=REPLACE(@weight,\',\',\'.\')*1)\n \n ORDER BY country.id DESC, postalcodefrom.id DESC, postalcodeto.id DESC, weightfrom.id DESC, weightto.id DESC, orderamountstart.id DESC, orderamountend.id DESC, deliverymethod.id DESC\n','<shippingCostDirectoryId>',@shippingCostDirectoryId));


